import { useState } from "react";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import  { Room, Star } from "@material-ui/icons";

import "./app.scss";

function App() {
  const [viewport, setViewport] = useState({
    width: "100vw",
    height: "100vh",
    latitude: 46,
    longitude: 17,
    zoom: 4
  });

  return (
    <div className="app">
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
        onViewportChange={nextViewport => setViewport(nextViewport)}
        mapStyle="mapbox://styles/safak/cknndpyfq268f17p53nmpwira"
      >
        <Marker 
          latitude={44.4275} 
          longitude={26.0874} 
          offsetLeft={-20} 
          offsetTop={-10}
        >
          <Room style={{ fontSize: viewport.zoom * 7, color: "slateblue" }}/>
        </Marker>
        <Popup
          latitude={44.4275}
          longitude={26.0874}
          closeButton={true}
          closeOnClick={false}
          anchor="left" 
          >
          <div className="card">
            <label>Place</label>
            <h4 className="place">Palace of Parliament</h4>
            <label>Review</label>
            <p className="desc">The second biggest building in the world (after Pentagon, US).</p>
            <label>Rating</label>
            <div className="stars">
              <Star className="star" />
              <Star className="star" />
              <Star className="star" />
              <Star className="star" />
              <Star className="star" />
            </div>
            <label>Information</label>
            <span className="username">Created by <b>kTz</b></span>
            <span className="date">1 hour ago</span>
          </div>
        </Popup>
      </ReactMapGL>
    </div>
  );
}

export default App;
